  async updateScan(
    id: string,
    updates: Partial<Omit<Scan, "id">>,
  ): Promise<Scan | undefined> {
    const existing = this.scans.get(id);
    if (!existing) {
      return undefined;
    }

    const merged: Scan = {
      ...existing,
      ...updates,
    };

    if (updates.status && !scanStatusEnumValues.includes(updates.status)) {
      throw new Error(`Invalid scan status: ${updates.status}`);
    }

    // Auto-set completedAt when moving to completed.
    if (existing.status !== "completed" && updates.status === "completed") {
      if (merged.completedAt == null) {
        merged.completedAt = new Date();
      }
    }

    if (updates.createdAt && !(updates.createdAt instanceof Date)) {
      merged.createdAt = new Date(updates.createdAt);
    }

    if (updates.completedAt !== undefined && updates.completedAt !== null) {
      merged.completedAt =
        updates.completedAt instanceof Date
          ? updates.completedAt
          : new Date(updates.completedAt);
    } else if (updates.completedAt === null) {
      merged.completedAt = null;
    }

    this.scans.set(id, merged);
    return merged;
  }
